import React, {Component} from "react";
import PropTypes from 'prop-types';

export default class TaskForm extends Component{

    //Estados
    state = {
        title:'',
        description:''
    }

    //Metodos
    onSubmit = (e) => {
        e.preventDefault();
    }

    render(){
        return(
            <form onSubmit={this.onSubmit}>
                <input type="text" placeholder="Ingrese una nueva tarea" />
                <br/>
                <textarea name="" id="" cols="30" rows="10" placeholder="Ingrese una descripción de la tarea" style={{marginTop:'20px'}}></textarea>
                <br/>
                <button type="submit" style={{marginTop:'20px'}}>Añadir tarea</button>
            </form>
        )
    }
        
}
